//传入阳历年 立春布尔值 返回年干 年支 年干支
//年干支
func yearGZ(year int, lcb bool) (string, string) {
	var aliasGan, aliasZhi string
	switch lcb {
	case false: //日期在立春之前
		//干
		g := 1 + (year+6)%10
		if g -= 1; g < 1 {
			g += 10
		}
		aliasGan = Gans[g] //Gan
		//支
		z := 1 + (year+8)%12
		if z -= 1; z < 1 {
			z += 12
		}
		aliasZhi = Zhis[z]
	case true: //日期在立春之后
		yearg := 1 + (year+6)%10
		yearz := 1 + (year+8)%12
		aliasGan = Gans[yearg] //Gan
		aliasZhi = Zhis[yearz]
	}

	return aliasGan, aliasZhi

}

//传入指定时间　立春布尔值　立春日时间戳　节气布尔值　节气索引值
func monthGZ(cust time.Time, lcb bool, jieqib bool, index int) string {
	b := jieqib
	yg, _ := yearGZ(cust.Year(), lcb)
	gzArr := mgzArr(yg)

	if (b == false && index == 0) && lcb == false { //在本年立春之前
		index -= 1
		if index < 0 {
			index += 12
		}
		index -= 1
	} else if (b == false && index == 0) && lcb == true {
		index -= 1
		if index < 0 {
			index += 12
		}
	} else if b == true {
		index -= 1
		if index < 0 {
			index += 12
		}
	}
	return gzArr[index]
}

//传入阳历日期 返回日干支 日干数字 1甲 2乙 3丙...10癸
func dayGanZhi(year, month, day int) (string, string) {
	t := time.Date(year, time.Month(month), day, 0, 0, 0, 0, time.Local)
	jd := calendar.Date2JDE(t)
	jdI := int(math.Ceil(jd)) //>=
	gn := 1 + (jdI%60-1)%10   //干
	if gn == 0 {
		gn += 10
	}
	z := 1 + (jdI%60+1)%12 //支
	//g 日干数字
	daygM := Gans[gn]
	dayzM := Zhis[z]
	dgz := daygM + dayzM
	return dgz, daygM
}

